import { Component, Input, NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'child',
  template: `<h2>Child {{ prop1 }}</h2>`
})
export class ChildComponent {
  @Input() prop1: number
}

@Component({
  selector: 'my-app',
  template: `
   <h2 *ngIf="t">Parent</h2>
   <child [prop1]="x"></child>

<button (click)="t = true">Toggle</button>
  `
})
export class AppComponent {
  x = 1;

t: boolean;
}


@NgModule({
  declarations: [
    ChildComponent,
    AppComponent
  ],
  imports: [CommonModule]
})
export class MyModule {}

